svg.script:
var dsyim_image = document.getElementById("DEOKSUYI");
var dsyim = document.getElementById("DSYIM");

var configs = window.location.hash.replace("#","").split("&");
console.log("total hash item: "+configs.length);
for (var i=0; i < configs.length; i++) {
  var item = configs[i].split("=");
  if (item.length > 1) {
    console.log("item["+i+"]: \""+item[0]+"\" = \""+item[1]+"\"");
    switch(item[0]) {
    case "size":
      dsyim_image.style.width=""+item[1]+"px";
      dsyim_image.style.height=""+item[1]+"px";
      // dot.style.r = parseInt(item[1]);
      break;
    case "bgcolor":
      dsyim.style.fill = item[1];
      break;
    case "color":
      dsyim.style.stroke = item[1];
      break;
    case "line":
      dsyim.style.strokeWidth = item[1];
      break;
    }
  }
}
